mapper.select:

select 

from BUYER_MSG_SETTING_REPORT

  
    and BUYER_OID = #{buyerOid,jdbcType=DECIMAL}
  
  
    and MSG_TYPE = #{msgType,jdbcType=VARCHAR}
  
  
    and SUB_TYPE = #{subType,jdbcType=VARCHAR}
  
  
    and CUSTOMIZED_REPORT = #{customizedReport,jdbcType=BIT}
  
  
    and REPORT_TEMPLATE = #{reportTemplate,jdbcType=VARCHAR}

mapper.delete:

delete from BUYER_MSG_SETTING_REPORT

  
    and BUYER_OID = #{buyerOid,jdbcType=DECIMAL}
  
  
    and MSG_TYPE = #{msgType,jdbcType=VARCHAR}
  
  
    and SUB_TYPE = #{subType,jdbcType=VARCHAR}
  
  
    and CUSTOMIZED_REPORT = #{customizedReport,jdbcType=BIT}
  
  
    and REPORT_TEMPLATE = #{reportTemplate,jdbcType=VARCHAR}

mapper.update:
update BUYER_MSG_SETTING_REPORT

  
    CUSTOMIZED_REPORT = #{customizedReport,jdbcType=BIT},
  
  
    REPORT_TEMPLATE = #{reportTemplate,jdbcType=VARCHAR}
  

where BUYER_OID = #{buyerOid,jdbcType=DECIMAL}
  and MSG_TYPE = #{msgType,jdbcType=VARCHAR}
  and SUB_TYPE = #{subType,jdbcType=VARCHAR}

update BUYER_MSG_SETTING_REPORT
set CUSTOMIZED_REPORT = #{customizedReport,jdbcType=BIT},
  REPORT_TEMPLATE = #{reportTemplate,jdbcType=VARCHAR}
where BUYER_OID = #{buyerOid,jdbcType=DECIMAL}
  and MSG_TYPE = #{msgType,jdbcType=VARCHAR}
  and SUB_TYPE = #{subType,jdbcType=VARCHAR}
